import React, { useEffect, useState, useRef } from 'react';
import {
  ClickAwayListener,
  Grow,
  Paper,
  Popper,
  MenuItem,
  MenuList,
  makeStyles,
} from '@material-ui/core';
import { ProfileIcon } from '../svg';
import { Link, Router } from '../../i18n';
import style from './header.module.scss';
import { useTranslation } from '../../i18n';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../../redux/actions/authActions/authActions';
const useStyles = makeStyles(() => ({
  paper: {
    borderRadius: '6px',
    borderTopLeftRadius: '0',
    borderTopRightRadius: '0',
    background: '#1d1d1d',
    color: '#fff',
    overflow: 'hidden',

    '& .MuiMenuItem-root': {
      padding: 0,
      minWidth: '120px',
    },
    '& .MuiMenuItem-root a': {
      color: '#fff',
      justifyContent: 'flex-start !important',
      padding: '0 20px',
      display: 'block !important',
      height: '44px !important',
      width: '100% !important',
      display: 'flex',
      alignItems: 'center',
      textDecoration: 'none',
    },
    '& .MuiMenuItem-root a:hover': {
      textDecoration: 'none',
    },
    '& .MuiMenuItem-root p': {
      color: '#fff',
      padding: '50px',
    },
    '& .MuiList-root': {
      padding: '0',
    },
    '& .MuiListItem-root:hover': {
      background: 'rgba(255,255,255,0.2)',
    },
  },
  popper: {
    zIndex: '999',
    paddingTop: '10px',
    top: '0 !important',
  },
}));
const ProfileDropdown = ({ title, subCategs }) => {
  const user = useSelector((state) => state.auth.user);
  const { t } = useTranslation();
  const [isBagVisible, setIsBagVisible] = useState(false);
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const anchorRef = useRef(null);

  const handleToggle = () => {
    setOpen((prevOpen) => !prevOpen);
  };

  const handleClose = (event) => {
    if (anchorRef.current && anchorRef.current.contains(event.target)) {
      return;
    }

    setOpen(false);
  };

  function handleListKeyDown(event) {
    if (event.key === 'Tab') {
      event.preventDefault();
      setOpen(false);
    }
  }
  const dispatch = useDispatch();
  const signOut = () => {
    dispatch(logout());
    Router.push('/');
  };

  const prevOpen = useRef(open);
  useEffect(() => {
    if (prevOpen.current === true && open === false) {
      anchorRef.current.focus();
    }

    prevOpen.current = open;
  }, [open]);

  return (
    <>
      <li
        className={`${style.listItem} ${style.cartIcon}`}
        ref={anchorRef}
        aria-controls={open ? 'menu-list-grow' : undefined}
        aria-haspopup='true'
        // onClick={handleToggle}
        onMouseEnter={() => setOpen(true)}
        onMouseLeave={() => setOpen(false)}
      >
        <Link href='/account'>
          <a aria-label='Profile'>
            <ProfileIcon />
          </a>
        </Link>
        <Popper
          open={open}
          anchorEl={anchorRef.current}
          className={classes.popper}
          role={undefined}
          transition
          disablePortal
        >
          {({ TransitionProps, placement }) => (
            <Grow
              {...TransitionProps}
              style={{
                transformOrigin:
                  placement === 'bottom' ? 'center top' : 'center bottom',
              }}
            >
              <Paper className={classes.paper}>
                <ClickAwayListener onClickAway={handleClose}>
                  <MenuList
                    autoFocusItem={open}
                    id='menu-list-grow'
                    onKeyDown={handleListKeyDown}
                  >
                    <MenuItem onClick={handleClose} diableRipple>
                      <Link href='/orders'>
                        <a>{t('orders')}</a>
                      </Link>
                    </MenuItem>
                    <MenuItem onClick={handleClose} diableRipple>
                      <Link href='/account'>
                        <a>{t('profile')}</a>
                      </Link>
                    </MenuItem>
                    {user ? (
                      <MenuItem onClick={signOut} diableRipple>
                        <Link href='/signin'>
                          <a>{t('signout')}</a>
                        </Link>
                      </MenuItem>
                    ) : (
                      <MenuItem onClick={handleClose} diableRipple>
                        <Link href='/signin'>
                          <a>{t('signin')}</a>
                        </Link>
                      </MenuItem>
                    )}
                    {/* <MenuItem onClick={handleClose} diableRipple>
                      <Link href='/'>
                        <a>{t('signout')}</a>
                      </Link>
                    </MenuItem> */}
                  </MenuList>
                </ClickAwayListener>
              </Paper>
            </Grow>
          )}
        </Popper>
      </li>
    </>
  );
};

export default ProfileDropdown;
