import { authActionTypes } from './authActionTypes'

export const setUser = (user) => {
  return {
    type: authActionTypes.SET_USER,
    payload: user,
  }
}

export const phoneAction = (phone) => {
  return {
    type: authActionTypes.PHONE,
    payload: phone,
  }
}

export const logout = () => ({
  type: authActionTypes.LOGOUT,
})
